function alert(param) {
	return '<div role="alert" class="alert red" style="color:white;">' + param +'</div>';
}

jQuery('#login_btn').click(function()
{
	var user = jQuery('#user').val(),
		pass = jQuery('#password').val();
	jQuery.ajax({
		url 		: '/api/login',
		method 		: 'post',
		data 		: 'username=' + user + '&password=' + pass,
		dataType 	: 'json',
		success 	: function(res)
		{
			if(res.error == 0)
			{
				jQuery('#notice').html(alert(res.message))
				setTimeout(function()
				{
					window.location.replace("./dashboard");
				}, 500);
			} else
			{
				for (var i = 0; i < res.message.length; i++)
				{
					jQuery('#notice').html(alert(res.message[i].msg))
				}
			}
		}
	});
})